nasal.load:
var livery_update = aircraft.livery_update.new("Aircraft/eurofighter/Models/Liveries", 10);
var me = cmdarg();
var callsign = me.getNode("callsign").getValue();
var init = func {
print("Initialising Typhoon Remote Functions ("~callsign~")");
#Strobes
var power = me.getNode("sim/multiplay/generic/float[5]", 1);
var lstrobe = me.getNode("sim/model/eurofighter/lights/strobe-lower", 1);
var ustrobe = me.getNode("sim/model/eurofighter/lights/strobe-upper", 1);
var rnd = rand();
#print("Random number is "~rnd~"!");
lstrand = 0.9 + ( rnd * 1.015 );
ustrand = 1.2 + (rnd);
#print("LSTRAND is "~lstrand~"!");
#print("USTRAND is "~ustrand~"!");
aircraft.light.new(ustrobe, [0.02, ustrand], power);
aircraft.light.new(lstrobe, [0.02, lstrand], power);
props.globals.initNode("/sim/time/aura-setting", 0.2, "DOUBLE");
lightaura();
var lightaura = func {
var rad = getprop("/sim/time/sun-angle-rad");
var fact = ((( 1 / rad ) * 1.25) - 0.25);
setprop("/sim/time/aura-dimming", fact);
settimer(lightaura, 2);
}
init();
